-- Copiar hash correto do Lucas para o Lúcio
UPDATE users 
SET passwordHash = (
  SELECT passwordHash 
  FROM (SELECT passwordHash FROM users WHERE email = 'lucas@example.com') AS temp
)
WHERE email = 'lucio@example.com';

-- Verificar atualização
SELECT email, role, LEFT(passwordHash, 30) as hash FROM users WHERE email IN ('lucio@example.com', 'lucas@example.com');

